import { createSlice } from '@reduxjs/toolkit'

export const referenceSlice = createSlice({
    name: 'reference',
    initialState: {
        reference: null,
        page: null,
        pageSize: 25
    },
    reducers: {
        // setReference: (state) => {
        //     console.log(state.action);
        // },
        reread: (state, action) => {
            state.reference.read(state.pageSize).then(function (page) {
                state.page = page;
            });
        },
        newPageSize: (state, action) => {
            state.pageSize = action.payload;
            // todo call reread reducer instead of reading here
            state.reference.read(action.payload).then(function (page) {
                state.page = page;
            })
        }
        // selectReference: (state) => {
        //     state.reference
        // }
    }
});

export const { reread, newPageSize } = referenceSlice.actions

export default referenceSlice.reducer;
