import { createSlice } from '@reduxjs/toolkit'

export const referenceSlice = createSlice({
    name: 'reference',
    initialState: {
        reference: null,
        page: null,
        pageSize: 25
    },
    // NOTE: no clue what 'state' variable is
    reducers: {
        getReference: (state, action) => {

        },
        setReference: (state, action) => {
            console.log("setReference payload: ", action);
            state.reference = action.resolvedReference;
            console.log(state.pageSize);
        },
        setPage: (state, action) => {
            state.page = action.refPage;
        },
        reread: (state, action) => {
            action.reference.read(state.pageSize).then(function (page) {
                state.page = page;
            });
        },
        newPageSize: (state, action) => {
            state.pageSize = action.payload;
            // todo call reread reducer instead of reading here
            state.reference.read(action.payload).then(function (page) {
                state.page = page;
            })
        }
        // selectReference: (state) => {
        //     state.reference
        // }
    }
});

export const { setReference, setPage, reread, newPageSize } = referenceSlice.actions

export default referenceSlice.reducer;
